            IERC1155(nftContract).safeTransferFrom(nftSeller, nftBuyer, nftId, 1, "0x");
        } else {
            IERC721(nftContract).safeTransferFrom(nftSeller, nftBuyer, nftId, "0x");
        }
    }
}

